INSERT INTO property( key, lang, value) VALUES('view.freq.info','PL','<span>Lista frekwencyjna jest lista wyrazów (w formie hasłowej lub dokładnie takiej, jak w tekście) wraz z częstością występowania.</span><span>System pozwala na eksport listy frekwencyjnej w formacie .CSV i jej przetwarzanie arkuszem kalkulacyjnym.</span><span>Tekst jest kodowany w formacie UTF-8.</span>');
